DROP TABLE IF EXISTS `gnc_moduller`;

CREATE TABLE `gnc_moduller` (
  `dosya_id` int(8) NOT NULL AUTO_INCREMENT,
  `dosya_adi` varchar(255) NOT NULL COMMENT 'anasayfa, giris vb… gorunum ve model dosyasında yer alan dosyaların sadece adı',
  `dosya_yayin_durumu` tinyint(2) DEFAULT '1' COMMENT 'Yayındaysa 1, değilse 0 ',
  `dosya_izin_durumu` tinyint(2) NOT NULL DEFAULT '0' COMMENT '0 ziyaretçi, 1-99 üye, 100-110 yönetici, 111+ geliştirici',
  `dosya_header` varchar(65) DEFAULT NULL COMMENT 'Eğer bu dosyada farklı bir header yüklenecekse, görünüm klasöründeki adresi belirtilmeli',
  `dosya_footer` varchar(65) DEFAULT NULL COMMENT 'Eğer bu dosyada farklı bir footer yüklenecekse, görünüm klasöründeki adresi belirtilmeli',
  `dosya_gorunum_cache` int(16) DEFAULT '0',
  `dosya_model_cache` int(16) DEFAULT '0',
  `dosya_header_cache` int(16) DEFAULT '0',
  `dosya_footer_cache` int(16) DEFAULT '0',
  PRIMARY KEY (`dosya_id`)
) ENGINE=InnoDB AUTO_INCREMENT=27 DEFAULT CHARSET=utf8 COMMENT='gorunum ve model içerisinde yer alan php dosyalarının yayın ve yetki durumlarının yer aldığı tablodur, güvenlik ve işlevsellik açısından çok önemlidir.\n\nBurada kayıtlı olmayan bir dosya çalıştırılamaz. Dolayısıyla yeni bir sayfa veya özellik eklemek istediğinizde bu tabloya kayıt girmek zorundasınız. Bunu yönetim panelinden kolayca yapabilirsiniz.\n\nNe yaptığınızı bilmiyorsanız tablo yapısı ile oynamayın. İçerisinde yer alan gnc_ ifadesi ile başlayan hiçbir şeyi silmeyiniz. Çünkü yönetim bölümü sistem ile entegre içinde yazılmış olup ayrıştırılamaz.';


