DROP TABLE IF EXISTS `gnc_veriler`;

CREATE TABLE `gnc_veriler` (
  `veri_id` int(11) NOT NULL AUTO_INCREMENT,
  `veri_adi` varchar(65) NOT NULL,
  `veri_yolu` varchar(255) NOT NULL,
  `veri_tipi` int(3) DEFAULT NULL COMMENT '1-> resim, 11-> dışardan resim, 2-> video, 21 -> dışardan video, 0->diğer',
  `veri_aciklama` varchar(255) DEFAULT NULL,
  `veri_tarih` varchar(65) DEFAULT NULL,
  PRIMARY KEY (`veri_id`)
) ENGINE=InnoDB AUTO_INCREMENT=146 DEFAULT CHARSET=utf8 COMMENT='Albüm, slide, carousel, dosya indirme vs. için kullanılmak üzere gnc ile yüklenen veriler ana dizindeki /veri/dosyalar klasörüne yerleştirmektedir. \n\n/veri/dosyalar klasörüne tüm dosyalar yüklenmekte olup eğer resimler için belirtilmişse thumbnail versiyonları /veri/dosyalar/thumb içerisine dosyanın ismi değiştirilmeksizin yüklenmektedir. Eğer dosya resim değilse ve buna rağmen thumb için bir ifade yazılmışsa, dosya''nın bir kopyası yine thumb içerisine yüklenecektir. Bu husus kullanıcıya bir yük getirmezken sunucuda gereksiz dosya barındıracağı için disk alanı kullanımını olumsuz etkileyecektir.\n\nMetin editorunden yuklenen resimler bu bölümden bağımsız tutulmuş olup, editor içerisinden yüklenen veriler yine /veri klasöründe yer alan /_images ve /_thumbs klasörüne yüklenmektedir.';


